import React from 'react';

export default function Navigation({ activeTab, onTabChange }) {
  const tabs = [
    { id: 'workouts', label: 'Workouts', icon: 'üí™' },
    { id: 'gym', label: 'Gym', icon: 'üèãÔ∏è' },
    { id: 'fasting', label: 'Fasting', icon: '‚è±' },
    { id: 'log', label: 'Log', icon: 'üìù' },
    { id: 'progress', label: 'Progress', icon: 'üìà' },
    { id: 'streak', label: 'Streak', icon: 'üî•' },
    { id: 'achievements', label: 'Achievements', icon: 'üèÜ' }
  ];

  return (
    <nav className="bg-white shadow-sm">
      <div className="overflow-x-auto">
        <div className="flex px-4 py-2 min-w-max">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => onTabChange(tab.id)}
              className={`px-4 py-2 mx-1 rounded-xl transition-colors ${
                activeTab === tab.id
                  ? 'bg-blue-500 text-white'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              <span className="mr-2">{tab.icon}</span>
              {tab.label}
            </button>
          ))}
        </div>
      </div>
    </nav>
  );
}
