import React, { useState } from 'react';
import './App.css';
import Navigation from './components/Navigation';
import FastingTimer from './components/FastingTimer';
import WorkoutLibrary from './components/WorkoutBrowser';
import ProgressTracker from './components/ProgressTracker';
import WorkoutLogger from './components/WorkoutLogger';
import StreakTracker from './components/StreakTracker';
import Achievements from './components/Achievements';
import QuickStart from './components/QuickStart';

function App() {
  const [activeSection, setActiveSection] = useState('workouts');
  const [userName] = useState('Nadia');

  const renderContent = () => {
    switch (activeSection) {
      case 'workouts':
        return (
          <div className="min-h-screen bg-gray-50 p-6">
            {/* Header */}
            <div className="mb-8">
              <h1 className="text-3xl font-bold text-gray-900">
                Hi {userName} ðŸ‘‹
              </h1>
              <p className="text-gray-600 mt-2">
                Ready for your next workout?
              </p>
            </div>

            {/* Main Actions Grid */}
            <div className="grid grid-cols-2 gap-4">
              <button
                onClick={() => setActiveSection('quickstart')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">âš¡</span>
                <h2 className="text-lg font-semibold text-gray-900">Quick Start</h2>
                <p className="text-sm text-gray-600 mt-1">Jump into a workout</p>
              </button>

              <button
                onClick={() => setActiveSection('browse')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">ðŸ“š</span>
                <h2 className="text-lg font-semibold text-gray-900">Browse</h2>
                <p className="text-sm text-gray-600 mt-1">Explore programs</p>
              </button>

              <button
                onClick={() => setActiveSection('logger')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">âœ¨</span>
                <h2 className="text-lg font-semibold text-gray-900">Log Workout</h2>
                <p className="text-sm text-gray-600 mt-1">Record your progress</p>
              </button>

              <button
                onClick={() => setActiveSection('progress')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">ðŸ“ˆ</span>
                <h2 className="text-lg font-semibold text-gray-900">Progress</h2>
                <p className="text-sm text-gray-600 mt-1">Track your journey</p>
              </button>
            </div>
          </div>
        );
      case 'fasting':
        return <FastingTimer />;
      case 'progress':
        return <ProgressTracker />;
      case 'achievements':
        return <Achievements />;
      case 'streak':
        return <StreakTracker />;
      case 'logger':
        return <WorkoutLogger />;
      case 'quickstart':
        return <QuickStart onBack={() => setActiveSection('workouts')} />;
      case 'browse':
        return <WorkoutLibrary onBack={() => setActiveSection('workouts')} />;
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navigation activeSection={activeSection} setActiveSection={setActiveSection} />
      <div className="px-4 py-4 sm:px-6 sm:py-6 md:px-8">
        <div className="max-w-7xl mx-auto">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default App;
