import React, { useState } from 'react';
import { workoutPlans } from '../data/workoutPlans';
import WorkoutLogger from './WorkoutLogger';

export default function WorkoutLibrary() {
  const [selectedWorkout, setSelectedWorkout] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showingWorkout, setShowingWorkout] = useState(true); // true for workout view, false for logger

  const filteredWorkouts = workoutPlans.filter(workout =>
    workout.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="max-w-6xl mx-auto p-4">
      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search workouts..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
        {/* Workout List */}
        <div className="bg-white rounded-xl shadow-lg p-4 h-[600px] overflow-y-auto">
          <h2 className="text-xl font-bold mb-4">Workout Plans</h2>
          <div className="space-y-2">
            {filteredWorkouts.map(workout => (
              <button
                key={workout.id}
                onClick={() => {
                  setSelectedWorkout(workout);
                  setShowingWorkout(true);
                }}
                className={`w-full text-left p-4 rounded-lg transition-colors ${
                  selectedWorkout?.id === workout.id
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-50 hover:bg-gray-100'
                }`}
              >
                <div className="font-semibold">{workout.name}</div>
                <div className="text-sm opacity-75">
                  {workout.exercises.length} exercises
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Main Content Area */}
        <div className="lg:col-span-2">
          {selectedWorkout ? (
            <div className="space-y-4">
              {/* Toggle Buttons */}
              <div className="bg-white rounded-xl shadow-lg p-2 flex">
                <button
                  onClick={() => setShowingWorkout(true)}
                  className={`flex-1 py-2 rounded-lg transition-colors ${
                    showingWorkout
                      ? 'bg-blue-500 text-white'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  View Workout
                </button>
                <button
                  onClick={() => setShowingWorkout(false)}
                  className={`flex-1 py-2 rounded-lg transition-colors ${
                    !showingWorkout
                      ? 'bg-blue-500 text-white'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  Log Workout
                </button>
              </div>

              {/* Content */}
              {showingWorkout ? (
                /* Workout Details */
                <div className="bg-white rounded-xl shadow-lg p-6">
                  <h2 className="text-xl font-bold mb-4">{selectedWorkout.name}</h2>
                  <div className="space-y-6">
                    {selectedWorkout.exercises.map((exercise, index) => (
                      <div key={index} className="bg-gray-50 rounded-lg p-4">
                        <h3 className="text-lg font-semibold mb-2">{exercise.name}</h3>
                        <div className="text-gray-600 mb-3">
                          {exercise.sets} sets Ã— {exercise.reps} reps
                        </div>
                        <div className="text-sm text-gray-500 mb-4">
                          {exercise.notes}
                        </div>
                        <div className="aspect-w-16 aspect-h-9">
                          <iframe
                            src={`https://www.youtube.com/embed/${exercise.videoId}`}
                            title={`How to do ${exercise.name}`}
                            className="w-full rounded-md"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                /* Workout Logger */
                <WorkoutLogger selectedWorkout={selectedWorkout} />
              )}
            </div>
          ) : (
            <div className="bg-white rounded-xl shadow-lg p-6 h-[600px] flex items-center justify-center text-gray-500">
              Select a workout to view details and log your progress
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
