import React, { useState } from 'react';
import './App.css';
import QuickStart from './components/QuickStart';
import WorkoutBrowser from './components/WorkoutBrowser';

function App() {
  const [currentView, setCurrentView] = useState('home');
  const [userName] = useState('Nadia');

  const renderView = () => {
    switch (currentView) {
      case 'quickstart':
        return <QuickStart onBack={() => setCurrentView('home')} />;
      case 'browse':
        return <WorkoutBrowser onBack={() => setCurrentView('home')} />;
      default:
        return (
          <div className="min-h-screen bg-gray-50 p-6">
            {/* Header */}
            <div className="mb-8">
              <h1 className="text-3xl font-bold text-gray-900">
                Hi {userName} üëã
              </h1>
              <p className="text-gray-600 mt-2">
                Ready for your next workout?
              </p>
            </div>

            {/* Main Actions Grid */}
            <div className="grid grid-cols-2 gap-4 mb-8">
              <button
                onClick={() => setCurrentView('quickstart')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">‚ö°</span>
                <h2 className="text-lg font-semibold text-gray-900">Quick Start</h2>
                <p className="text-sm text-gray-600 mt-1">Jump into a workout</p>
              </button>

              <button
                onClick={() => setCurrentView('browse')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">üìö</span>
                <h2 className="text-lg font-semibold text-gray-900">Browse</h2>
                <p className="text-sm text-gray-600 mt-1">Explore programs</p>
              </button>

              <button
                onClick={() => setCurrentView('custom')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">‚ú®</span>
                <h2 className="text-lg font-semibold text-gray-900">Custom</h2>
                <p className="text-sm text-gray-600 mt-1">Build your workout</p>
              </button>

              <button
                onClick={() => setCurrentView('favorites')}
                className="bg-white p-6 rounded-2xl shadow-sm hover:shadow-md transition-shadow"
              >
                <span className="text-4xl mb-4 block">‚ù§Ô∏è</span>
                <h2 className="text-lg font-semibold text-gray-900">Favorites</h2>
                <p className="text-sm text-gray-600 mt-1">Saved workouts</p>
              </button>
            </div>

            {/* Search Bar */}
            <div className="fixed bottom-6 left-6 right-6">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search exercises, workouts, or programs..."
                  className="w-full px-4 py-3 pl-10 bg-white rounded-xl shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <span className="absolute left-3 top-3.5 text-gray-400">
                  üîç
                </span>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="App">
      {renderView()}
    </div>
  );
}

export default App;
