import React, { useState } from 'react';
import QuickStart from './QuickStart';

export default function WorkoutLibrary() {
  const [currentView, setCurrentView] = useState('home');

  const actions = [
    {
      id: 'quick-start',
      icon: 'üèÉ‚Äç‚ôÄÔ∏è',
      label: 'Quick Start',
      description: 'Start a workout now',
      color: 'bg-blue-50'
    },
    {
      id: 'browse',
      icon: 'üìö',
      label: 'Browse',
      description: 'View workout library',
      color: 'bg-purple-50'
    },
    {
      id: 'custom',
      icon: '‚ú®',
      label: 'Custom',
      description: 'Create your own workout',
      color: 'bg-green-50'
    },
    {
      id: 'favorites',
      icon: '‚≠ê',
      label: 'Favorites',
      description: 'Your saved workouts',
      color: 'bg-yellow-50'
    }
  ];

  const handleActionClick = (actionId) => {
    setCurrentView(actionId);
  };

  if (currentView === 'quick-start') {
    return <QuickStart onBack={() => setCurrentView('home')} />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Welcome Header */}
      <div className="px-6 pt-12 pb-8">
        <h1 className="text-4xl font-bold text-gray-900 mb-2">
          Hi Nadia,
        </h1>
        <h2 className="text-3xl font-bold text-gray-900">
          How can I help you today?
        </h2>
      </div>

      {/* Action Grid */}
      <div className="px-6">
        <div className="grid grid-cols-2 gap-4">
          {actions.map(action => (
            <button
              key={action.id}
              onClick={() => handleActionClick(action.id)}
              className={`${action.color} rounded-2xl p-6 text-left transition-transform active:scale-95 hover:shadow-md`}
            >
              <div className="text-3xl mb-3">{action.icon}</div>
              <div className="text-xl font-semibold text-gray-900 mb-1">
                {action.label}
              </div>
              <div className="text-sm text-gray-600">
                {action.description}
              </div>
            </button>
          ))}
        </div>
      </div>

      {/* Search Bar */}
      <div className="fixed bottom-8 left-6 right-6">
        <div className="bg-white rounded-full shadow-lg px-4 py-3 flex items-center">
          <span className="text-gray-400 mr-3">üîç</span>
          <input
            type="text"
            placeholder="Search workouts..."
            className="flex-1 bg-transparent outline-none text-gray-700 placeholder-gray-400"
          />
          <button className="ml-3 bg-black text-white rounded-full w-8 h-8 flex items-center justify-center">
            +
          </button>
        </div>
      </div>
    </div>
  );
}
