import React from 'react';
import { defaultEquipment } from './GymEquipment';

function generateWorkout(selectedEquipmentIds) {
  const selectedEquipment = defaultEquipment.filter(eq => selectedEquipmentIds.includes(eq.id));

  // Group equipment by category
  const equipmentByCategory = selectedEquipment.reduce((acc, equipment) => {
    if (!acc[equipment.category]) {
      acc[equipment.category] = [];
    }
    acc[equipment.category].push(equipment);
    return acc;
  }, {});

  // Generate exercises for each category
  const workout = [];
  Object.entries(equipmentByCategory).forEach(([category, equipment]) => {
    // Randomly select 2-3 exercises from each category
    const numExercises = Math.floor(Math.random() * 2) + 2; // 2-3 exercises
    const selectedExercises = [...equipment]
      .sort(() => Math.random() - 0.5)
      .slice(0, Math.min(numExercises, equipment.length));

    selectedExercises.forEach(exercise => {
      workout.push({
        ...exercise,
        sets: Math.floor(Math.random() * 2) + 3, // 3-4 sets
        reps: Math.floor(Math.random() * 5) + 8, // 8-12 reps
      });
    });
  });

  return workout;
}

export default function WorkoutPlan({ selectedEquipment }) {
  const workout = generateWorkout(selectedEquipment);

  if (selectedEquipment.length === 0) {
    return (
      <div className="p-4 text-center text-gray-500">
        Please select some equipment to generate a workout plan.
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Your Workout Plan</h2>
      <div className="space-y-6">
        {workout.map((exercise, index) => (
          <div key={index} className="bg-white rounded-lg shadow-md p-4">
            <div className="flex items-center justify-between mb-2">
              <h3 className="text-lg font-semibold">{exercise.name}</h3>
              <span className="text-sm bg-blue-100 text-blue-800 px-2 py-1 rounded">
                {exercise.category}
              </span>
            </div>
            <div className="text-gray-600 mb-3">
              {exercise.sets} sets Ã— {exercise.reps} reps
            </div>
            <div className="aspect-w-16 aspect-h-9">
              <iframe
                src={`https://www.youtube.com/embed/${exercise.videoId}`}
                title={`How to do ${exercise.name}`}
                className="w-full rounded-md"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
