import React, { useState, useEffect } from 'react';

export default function FastingTimer() {
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [isFasting, setIsFasting] = useState(false);
  const [duration, setDuration] = useState(0);
  const [fastHistory, setFastHistory] = useState(() => {
    const saved = localStorage.getItem('fastHistory');
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    let timer;
    if (isFasting && startTime) {
      timer = setInterval(() => {
        setDuration(Date.now() - startTime);
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isFasting, startTime]);

  const formatTime = ms => {
    const totalSec = Math.floor(ms / 1000);
    const hrs = Math.floor(totalSec / 3600);
    const mins = Math.floor((totalSec % 3600) / 60);
    return `${hrs}h ${mins}m`;
  };

  const startFast = () => {
    const now = Date.now();
    setStartTime(now);
    setIsFasting(true);
  };

  const endFast = () => {
    const now = Date.now();
    setEndTime(now);
    setIsFasting(false);
    const fastDuration = now - startTime;
    const newLog = {
      id: Date.now(),
      start: new Date(startTime).toLocaleString(),
      end: new Date(now).toLocaleString(),
      duration: formatTime(fastDuration),
    };
    const updated = [newLog, ...fastHistory];
    setFastHistory(updated);
    localStorage.setItem('fastHistory', JSON.stringify(updated));
    setStartTime(null);
    setDuration(0);
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h1 className="text-xl font-bold mb-2">‚è±Ô∏è Fasting Timer</h1>

      {isFasting ? (
        <div>
          <p className="text-green-600 font-semibold">Fasting for: {formatTime(duration)}</p>
          <button
            onClick={endFast}
            className="bg-red-500 text-white px-4 py-2 mt-2 rounded"
          >
            End Fast
          </button>
        </div>
      ) : (
        <button
          onClick={startFast}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Start Fast
        </button>
      )}

      <div className="mt-6">
        <h2 className="font-semibold mb-1">üóìÔ∏è Fast History</h2>
        <ul className="text-sm space-y-2">
          {fastHistory.map(fast => (
            <li key={fast.id} className="border-b pb-2">
              <strong>{fast.duration}</strong><br />
              {fast.start} ‚Üí {fast.end}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
